/**
 * P4 RED REGNANT - SCREAM_PHANTOM Detector (Port 3)
 * 
 * @port 3
 * @commander SPORE_STORM (aligned)
 * @verb DELIVER
 * @tier BRONZE
 * @provenance: .kiro/specs/red-regnant-8-screams/design.md
 * Validates: Requirements 4.1, 4.2, 4.3, 4.4, 4.5
 * 
 * Detects external dependencies and supply chain risks:
 * - CDN dependencies
 * - Unpinned npm dependencies
 * - External API calls
 * - Dynamic imports from URLs
 */

import * as fs from 'node:fs';
import * as path from 'node:path';
import {
  Detector,
  DetectorConfig,
  DetectorResult,
  DEFAULT_DETECTOR_CONFIG,
  mergeConfig,
  shouldScanFile,
  createEmptyResult,
} from '../contracts/detector.js';
import { createScreamReceipt, ScreamReceipt } from '../contracts/screams.js';

// --- PHANTOM PATTERNS ---

/**
 * Patterns that indicate external dependencies or supply chain risks
 */
export const PHANTOM_PATTERNS = {
  // CDN script tags
  CDN_SCRIPT: /<script[^>]+src=["']https?:\/\/[^"']+["'][^>]*>/gi,
  
  // CDN link tags
  CDN_LINK: /<link[^>]+href=["']https?:\/\/[^"']+["'][^>]*>/gi,
  
  // Dynamic import from URL
  DYNAMIC_IMPORT_URL: /import\s*\(\s*["'`]https?:\/\//gi,
  
  // Fetch to external API
  FETCH_EXTERNAL: /fetch\s*\(\s*["'`]https?:\/\/(?!localhost|127\.0\.0\.1)/gi,
  
  // Axios to external API
  AXIOS_EXTERNAL: /axios\.(get|post|put|delete|patch)\s*\(\s*["'`]https?:\/\/(?!localhost|127\.0\.0\.1)/gi,
  
  // XMLHttpRequest to external
  XHR_EXTERNAL: /\.open\s*\(\s*["'][^"']+["']\s*,\s*["'`]https?:\/\/(?!localhost|127\.0\.0\.1)/gi,
  
  // eval() usage (code injection risk)
  EVAL_USAGE: /\beval\s*\(/g,
  
  // new Function() usage (code injection risk)
  NEW_FUNCTION: /new\s+Function\s*\(/g,
  
  // document.write (XSS risk)
  DOCUMENT_WRITE: /document\.write\s*\(/g,
  
  // innerHTML assignment (XSS risk)
  INNER_HTML: /\.innerHTML\s*=/g,
} as const;

export type PhantomPatternName = keyof typeof PHANTOM_PATTERNS;

// --- PACKAGE.JSON PATTERNS ---

const UNPINNED_VERSION = /["'][\^~><=]/;
const LATEST_VERSION = /["']latest["']/i;
const STAR_VERSION = /["']\*["']/;

// --- DETECTOR IMPLEMENTATION ---

export class PhantomDetector implements Detector {
  readonly name = 'SCREAM_PHANTOM';
  readonly port = 3;
  readonly screamType = 'SCREAM_PHANTOM' as const;
  readonly description = 'Detects external dependencies and supply chain risks';

  async detect(
    targetPath: string,
    config?: Partial<DetectorConfig>
  ): Promise<DetectorResult> {
    const startTime = Date.now();
    const mergedConfig = mergeConfig(DEFAULT_DETECTOR_CONFIG, config);
    
    if (!mergedConfig.enabled) {
      return {
        ...createEmptyResult(this.screamType),
        duration: Date.now() - startTime,
      };
    }

    const receipts: ScreamReceipt[] = [];
    let filesScanned = 0;

    const walk = (dir: string) => {
      if (!fs.existsSync(dir)) return;
      
      const entries = fs.readdirSync(dir, { withFileTypes: true });
      
      for (const entry of entries) {
        const fullPath = path.join(dir, entry.name);
        
        // Skip excluded directories
        if (entry.isDirectory()) {
          if (!mergedConfig.excludeDirs.includes(entry.name)) {
            walk(fullPath);
          }
          continue;
        }

        // Special handling for package.json
        if (entry.name === 'package.json') {
          filesScanned++;
          try {
            const content = fs.readFileSync(fullPath, 'utf8');
            const pkgReceipts = this.scanPackageJson(fullPath, content, mergedConfig);
            receipts.push(...pkgReceipts);
          } catch {
            continue;
          }
          continue;
        }

        // Check if file should be scanned
        if (!shouldScanFile(fullPath, entry.name, mergedConfig)) {
          continue;
        }

        filesScanned++;
        
        try {
          const content = fs.readFileSync(fullPath, 'utf8');
          const fileReceipts = this.scanContent(fullPath, content, mergedConfig);
          receipts.push(...fileReceipts);
        } catch {
          continue;
        }
      }
    };

    // Handle both file and directory targets
    if (fs.existsSync(targetPath)) {
      const stat = fs.statSync(targetPath);
      if (stat.isDirectory()) {
        walk(targetPath);
      } else {
        filesScanned = 1;
        const content = fs.readFileSync(targetPath, 'utf8');
        
        if (targetPath.endsWith('package.json')) {
          const pkgReceipts = this.scanPackageJson(targetPath, content, mergedConfig);
          receipts.push(...pkgReceipts);
        } else {
          const fileReceipts = this.scanContent(targetPath, content, mergedConfig);
          receipts.push(...fileReceipts);
        }
      }
    }

    return {
      screamType: this.screamType,
      receipts,
      filesScanned,
      violationsFound: receipts.length,
      duration: Date.now() - startTime,
    };
  }

  /**
   * Scan file content for phantom patterns
   */
  private scanContent(
    filePath: string,
    content: string,
    config: DetectorConfig
  ): ScreamReceipt[] {
    const receipts: ScreamReceipt[] = [];

    for (const [patternName, pattern] of Object.entries(PHANTOM_PATTERNS)) {
      pattern.lastIndex = 0;
      
      const matches = content.match(pattern);
      if (matches && matches.length > 0) {
        const lineNumbers = this.findLineNumbers(content, pattern);
        
        receipts.push(createScreamReceipt(
          'SCREAM_PHANTOM',
          3,
          filePath,
          {
            phantomType: patternName,
            pattern: pattern.source,
            matchCount: matches.length,
            lineNumbers,
            severity: this.getSeverityForPattern(patternName as PhantomPatternName),
          },
          config.severity
        ));
      }
    }

    return receipts;
  }

  /**
   * Scan package.json for unpinned dependencies
   */
  private scanPackageJson(
    filePath: string,
    content: string,
    config: DetectorConfig
  ): ScreamReceipt[] {
    const receipts: ScreamReceipt[] = [];

    try {
      const pkg = JSON.parse(content);
      const depSections = ['dependencies', 'devDependencies', 'peerDependencies'];
      
      for (const section of depSections) {
        const deps = pkg[section];
        if (!deps || typeof deps !== 'object') continue;
        
        const unpinnedDeps: string[] = [];
        
        for (const [name, version] of Object.entries(deps)) {
          if (typeof version !== 'string') continue;
          
          if (UNPINNED_VERSION.test(`"${version}"`) || 
              LATEST_VERSION.test(`"${version}"`) ||
              STAR_VERSION.test(`"${version}"`)) {
            unpinnedDeps.push(`${name}@${version}`);
          }
        }
        
        if (unpinnedDeps.length > 0) {
          receipts.push(createScreamReceipt(
            'SCREAM_PHANTOM',
            3,
            filePath,
            {
              phantomType: 'UNPINNED_DEPENDENCY',
              section,
              unpinnedDeps,
              count: unpinnedDeps.length,
              message: `Found ${unpinnedDeps.length} unpinned dependencies in ${section}`,
              severity: 'warning',
            },
            'warning'
          ));
        }
      }
    } catch {
      // Invalid JSON, skip
    }

    return receipts;
  }

  /**
   * Find line numbers where pattern matches
   */
  private findLineNumbers(content: string, pattern: RegExp): number[] {
    const lines = content.split('\n');
    const lineNumbers: number[] = [];
    
    const flags = pattern.flags.replace('g', '');
    const linePattern = new RegExp(pattern.source, flags);
    
    for (let i = 0; i < lines.length; i++) {
      if (linePattern.test(lines[i])) {
        lineNumbers.push(i + 1);
      }
    }
    
    return lineNumbers;
  }

  /**
   * Get severity based on pattern type
   */
  private getSeverityForPattern(patternName: PhantomPatternName): 'warning' | 'error' | 'critical' {
    switch (patternName) {
      case 'EVAL_USAGE':
      case 'NEW_FUNCTION':
        return 'critical';
      case 'CDN_SCRIPT':
      case 'CDN_LINK':
      case 'DYNAMIC_IMPORT_URL':
        return 'error';
      case 'DOCUMENT_WRITE':
      case 'INNER_HTML':
        return 'error';
      case 'FETCH_EXTERNAL':
      case 'AXIOS_EXTERNAL':
      case 'XHR_EXTERNAL':
        return 'warning';
      default:
        return 'warning';
    }
  }
}

// --- FACTORY FUNCTION ---

export function createPhantomDetector(): Detector {
  return new PhantomDetector();
}

detectors/pollution.ts.source:
/**
 * P4 RED REGNANT - SCREAM_POLLUTION Detector (Port 5)
 * 
 * @port 5
 * @commander PYRE_PRAETORIAN (aligned)
 * @verb IMMUNIZE
 * @tier BRONZE
 * @provenance: .kiro/specs/red-regnant-8-screams/design.md
 * Validates: Requirements 6.1, 6.2, 6.3, 6.4, 6.5
 * 
 * Detects root pollution and medallion violations:
 * - Unauthorized root files
 * - Recursive sandbox loops
 * - Medallion tier violations
 * - PARA structure violations
 */

import * as fs from 'node:fs';
import * as path from 'node:path';
import {
  Detector,
  DetectorConfig,
  DetectorResult,
  DEFAULT_DETECTOR_CONFIG,
  mergeConfig,
  createEmptyResult,
} from '../contracts/detector.js';
import { createScreamReceipt, ScreamReceipt } from '../contracts/screams.js';

// --- ROOT WHITELIST ---

/**
 * Files/folders allowed in root directory per AGENTS.md
 */
export const ROOT_WHITELIST = [
  'hot_obsidian_sandbox',
  'cold_obsidian_sandbox',
  'AGENTS.md',
  'llms.txt',
  'ROOT_GOVERNANCE_MANIFEST.md',
  // ttao-notes-*.md pattern handled separately
  '.git',
  '.gitignore',
  '.github',
  '.husky',
  '.kiro',
  '.vscode',
  '.venv',
  '.env',
  'node_modules',
  'package.json',
  'package-lock.json',
  'reports',
  // Stryker temp directories
  '.stryker-tmp',
  '.stryker-tmp-p1',
  // Config files
  'stryker.p1.config.mjs',
  'vitest.config.ts',
  'tsconfig.json',
] as const;

// --- PARA STRUCTURE ---

export const PARA_FOLDERS = ['1_projects', '2_areas', '3_resources', '4_archive'] as const;

// --- MEDALLION TIERS ---

export const MEDALLION_TIERS = ['gold', 'silver', 'bronze'] as const;

// --- DETECTOR IMPLEMENTATION ---

export class PollutionDetector implements Detector {
  readonly name = 'SCREAM_POLLUTION';
  readonly port = 5;
  readonly screamType = 'SCREAM_POLLUTION' as const;
  readonly description = 'Detects root pollution and medallion violations';

  async detect(
    targetPath: string,
    config?: Partial<DetectorConfig>
  ): Promise<DetectorResult> {
    const startTime = Date.now();
    const mergedConfig = mergeConfig(DEFAULT_DETECTOR_CONFIG, config);
    
    if (!mergedConfig.enabled) {
      return {
        ...createEmptyResult(this.screamType),
        duration: Date.now() - startTime,
      };
    }

    const receipts: ScreamReceipt[] = [];
    let filesScanned = 0;

    if (!fs.existsSync(targetPath)) {
      return {
        screamType: this.screamType,
        receipts,
        filesScanned,
        violationsFound: 0,
        duration: Date.now() - startTime,
      };
    }

    const stat = fs.statSync(targetPath);
    
    if (stat.isDirectory()) {
      // Check root pollution
      const rootReceipts = this.checkRootPollution(targetPath, mergedConfig);
      receipts.push(...rootReceipts);
      filesScanned += rootReceipts.length > 0 ? 1 : 0;

      // Check sandbox structure
      const sandboxReceipts = this.checkSandboxStructure(targetPath, mergedConfig);
      receipts.push(...sandboxReceipts);
      filesScanned += sandboxReceipts.length > 0 ? 1 : 0;

      // Check for recursive sandbox loops
      const loopReceipts = this.checkRecursiveLoops(targetPath, mergedConfig);
      receipts.push(...loopReceipts);
      filesScanned += loopReceipts.length > 0 ? 1 : 0;
    } else {
      // Single file - check if it's in wrong location
      filesScanned = 1;
      const fileReceipts = this.checkFileLocation(targetPath, mergedConfig);
      receipts.push(...fileReceipts);
    }

    return {
      screamType: this.screamType,
      receipts,
      filesScanned,
      violationsFound: receipts.length,
      duration: Date.now() - startTime,
    };
  }

  /**
   * Check for unauthorized files in root directory
   */
  private checkRootPollution(
    rootPath: string,
    config: DetectorConfig
  ): ScreamReceipt[] {
    const receipts: ScreamReceipt[] = [];
    
    try {
      const entries = fs.readdirSync(rootPath, { withFileTypes: true });
      
      for (const entry of entries) {
        const name = entry.name;
        
        // Check whitelist
        if (this.isWhitelisted(name)) {
          continue;
        }
        
        // Check ttao-notes pattern
        if (/^ttao-notes-.*\.md$/.test(name)) {
          continue;
        }
        
        receipts.push(createScreamReceipt(
          'SCREAM_POLLUTION',
          5,
          path.join(rootPath, name),
          {
            pollutionType: 'ROOT_POLLUTION',
            fileName: name,
            isDirectory: entry.isDirectory(),
            message: `Unauthorized ${entry.isDirectory() ? 'directory' : 'file'} in root: ${name}`,
            severity: 'error',
          },
          config.severity
        ));
      }
    } catch {
      // Ignore read errors
    }
    
    return receipts;
  }

  /**
   * Check sandbox PARA structure
   */
  private checkSandboxStructure(
    rootPath: string,
    config: DetectorConfig
  ): ScreamReceipt[] {
    const receipts: ScreamReceipt[] = [];
    
    const sandboxes = ['hot_obsidian_sandbox', 'cold_obsidian_sandbox'];
    
    for (const sandbox of sandboxes) {
      const sandboxPath = path.join(rootPath, sandbox);
      
      if (!fs.existsSync(sandboxPath)) {
        continue;
      }
      
      // Check each medallion tier
      for (const tier of MEDALLION_TIERS) {
        const tierPath = path.join(sandboxPath, tier);
        
        if (!fs.existsSync(tierPath)) {
          continue;
        }
        
        // Check PARA structure
        const paraReceipts = this.checkParaStructure(tierPath, sandbox, tier, config);
        receipts.push(...paraReceipts);
      }
    }
    
    return receipts;
  }

  /**
   * Check PARA folder structure within a medallion tier
   */
  private checkParaStructure(
    tierPath: string,
    sandbox: string,
    tier: string,
    config: DetectorConfig
  ): ScreamReceipt[] {
    const receipts: ScreamReceipt[] = [];
    
    try {
      const entries = fs.readdirSync(tierPath, { withFileTypes: true });
      
      for (const entry of entries) {
        // Skip non-directories and special files
        if (!entry.isDirectory()) {
          // Allow certain files at tier level
          if (entry.name.endsWith('.jsonl') || entry.name === 'README.md') {
            continue;
          }
          
          receipts.push(createScreamReceipt(
            'SCREAM_POLLUTION',
            5,
            path.join(tierPath, entry.name),
            {
              pollutionType: 'PARA_VIOLATION',
              sandbox,
              tier,
              fileName: entry.name,
              message: `File at tier level should be in PARA folder: ${entry.name}`,
              severity: 'warning',
            },
            'warning'
          ));
          continue;
        }
        
        // Check if directory follows PARA naming
        const isParaFolder = PARA_FOLDERS.some(p => entry.name === p);
        
        if (!isParaFolder) {
          receipts.push(createScreamReceipt(
            'SCREAM_POLLUTION',
            5,
            path.join(tierPath, entry.name),
            {
              pollutionType: 'PARA_VIOLATION',
              sandbox,
              tier,
              folderName: entry.name,
              expectedFolders: [...PARA_FOLDERS],
              message: `Non-PARA folder in ${tier}: ${entry.name}`,
              severity: 'warning',
            },
            'warning'
          ));
        }
      }
    } catch {
      // Ignore read errors
    }
    
    return receipts;
  }

  /**
   * Check for recursive sandbox loops (sandbox within sandbox)
   */
  private checkRecursiveLoops(
    rootPath: string,
    config: DetectorConfig
  ): ScreamReceipt[] {
    const receipts: ScreamReceipt[] = [];
    
    const sandboxes = ['hot_obsidian_sandbox', 'cold_obsidian_sandbox'];
    
    for (const sandbox of sandboxes) {
      const sandboxPath = path.join(rootPath, sandbox);
      
      if (!fs.existsSync(sandboxPath)) {
        continue;
      }
      
      // Recursively check for nested sandboxes
      const nestedReceipts = this.findNestedSandboxes(sandboxPath, sandbox, config);
      receipts.push(...nestedReceipts);
    }
    
    return receipts;
  }

  /**
   * Find nested sandbox directories (recursive loop detection)
   */
  private findNestedSandboxes(
    dir: string,
    parentSandbox: string,
    config: DetectorConfig,
    depth: number = 0
  ): ScreamReceipt[] {
    const receipts: ScreamReceipt[] = [];
    
    if (depth > 10) return receipts; // Prevent infinite recursion
    
    try {
      const entries = fs.readdirSync(dir, { withFileTypes: true });
      
      for (const entry of entries) {
        if (!entry.isDirectory()) continue;
        
        // Skip excluded directories
        if (['node_modules', '.git', '4_archive', 'quarantine'].includes(entry.name)) {
          continue;
        }
        
        const fullPath = path.join(dir, entry.name);
        
        // Check for nested sandbox
        if (entry.name === 'hot_obsidian_sandbox' || entry.name === 'cold_obsidian_sandbox') {
          receipts.push(createScreamReceipt(
            'SCREAM_POLLUTION',
            5,
            fullPath,
            {
              pollutionType: 'RECURSIVE_LOOP',
              parentSandbox,
              nestedSandbox: entry.name,
              depth: depth + 1,
              message: `Recursive sandbox detected: ${entry.name} inside ${parentSandbox}`,
              severity: 'critical',
            },
            'critical'
          ));
        }
        
        // Continue searching
        const nested = this.findNestedSandboxes(fullPath, parentSandbox, config, depth + 1);
        receipts.push(...nested);
      }
    } catch {
      // Ignore read errors
    }
    
    return receipts;
  }

  /**
   * Check if a single file is in the correct location
   */
  private checkFileLocation(
    filePath: string,
    config: DetectorConfig
  ): ScreamReceipt[] {
    const receipts: ScreamReceipt[] = [];
    
    // Check if file is in a sandbox
    const inHotSandbox = filePath.includes('hot_obsidian_sandbox');
    const inColdSandbox = filePath.includes('cold_obsidian_sandbox');
    
    if (!inHotSandbox && !inColdSandbox) {
      // File is outside sandboxes - check if it's whitelisted
      const fileName = path.basename(filePath);
      if (!this.isWhitelisted(fileName) && !/^ttao-notes-.*\.md$/.test(fileName)) {
        receipts.push(createScreamReceipt(
          'SCREAM_POLLUTION',
          5,
          filePath,
          {
            pollutionType: 'OUTSIDE_SANDBOX',
            fileName,
            message: `File outside sandbox: ${fileName}`,
            severity: 'warning',
          },
          'warning'
        ));
      }
    }
    
    return receipts;
  }

  /**
   * Check if name is in whitelist
   */
  private isWhitelisted(name: string): boolean {
    return ROOT_WHITELIST.includes(name as typeof ROOT_WHITELIST[number]) ||
           name.startsWith('.stryker-tmp');
  }
}

// --- FACTORY FUNCTION ---

export function createPollutionDetector(): Detector {
  return new PollutionDetector();
}

detectors/theater.ts.source:
/**
 * P4 RED REGNANT - SCREAM_THEATER Detector (Port 2)
 * 
 * @port 2
 * @commander MIRROR_MAGUS (aligned)
 * @verb SHAPE
 * @tier BRONZE
 * @provenance: .kiro/specs/red-regnant-8-screams/design.md
 * Validates: Requirements 3.1, 3.2, 3.3, 3.4, 3.5, 3.6
 * 
 * Detects fake tests and mock poisoning:
 * - Assertionless tests
 * - Mock overuse (>5 mocks)
 * - Placeholder patterns
 * - 99%+ mutation scores (suspiciously perfect)
 */

import * as fs from 'node:fs';
import * as path from 'node:path';
import {
  Detector,
  DetectorConfig,
  DetectorResult,
  DEFAULT_DETECTOR_CONFIG,
  mergeConfig,
  shouldScanFile,
  createEmptyResult,
} from '../contracts/detector.js';
import { createScreamReceipt, ScreamReceipt } from '../contracts/screams.js';

// --- THEATER PATTERNS ---

/**
 * Patterns that indicate fake tests or placeholder code
 */
export const THEATER_PATTERNS = {
  // Placeholder implementations
  NOT_IMPLEMENTED: /throw new Error\(['"`]Not implemented['"`]\)/gi,
  
  // Logic placeholder comments
  LOGIC_PLACEHOLDER: /\/\/\s*(Logic goes here|TODO:?\s*implement)/gi,
  
  // Stub returns
  STUB_RETURN: /return\s+(null|undefined|false|0|\{\});\s*\/\/\s*stub/gi,
  
  // Implementation placeholder
  IMPL_PLACEHOLDER: /implementation\s+(below|here).*\.\.\./gis,
  
  // Empty test body
  EMPTY_TEST: /it\s*\([^)]+,\s*\(\)\s*=>\s*\{\s*\}\s*\)/g,
  
  // Test with only console.log
  CONSOLE_ONLY_TEST: /it\s*\([^)]+,\s*\(\)\s*=>\s*\{\s*console\.(log|warn|info)\([^)]*\);\s*\}\s*\)/g,
  
  // Skip markers without reason
  SKIP_NO_REASON: /\.(skip|todo)\s*\(\s*['"`][^'"`]+['"`]\s*,/g,
} as const;

export type TheaterPatternName = keyof typeof THEATER_PATTERNS;

// --- MOCK THRESHOLD ---
const MAX_MOCKS_PER_FILE = 5;

// --- DETECTOR IMPLEMENTATION ---

export class TheaterDetector implements Detector {
  readonly name = 'SCREAM_THEATER';
  readonly port = 2;
  readonly screamType = 'SCREAM_THEATER' as const;
  readonly description = 'Detects fake tests, mock poisoning, and placeholder code';

  async detect(
    targetPath: string,
    config?: Partial<DetectorConfig>
  ): Promise<DetectorResult> {
    const startTime = Date.now();
    const mergedConfig = mergeConfig(DEFAULT_DETECTOR_CONFIG, config);
    
    if (!mergedConfig.enabled) {
      return {
        ...createEmptyResult(this.screamType),
        duration: Date.now() - startTime,
      };
    }

    const receipts: ScreamReceipt[] = [];
    let filesScanned = 0;

    const walk = (dir: string) => {
      if (!fs.existsSync(dir)) return;
      
      const entries = fs.readdirSync(dir, { withFileTypes: true });
      
      for (const entry of entries) {
        const fullPath = path.join(dir, entry.name);
        
        // Skip excluded directories
        if (entry.isDirectory()) {
          if (!mergedConfig.excludeDirs.includes(entry.name)) {
            walk(fullPath);
          }
          continue;
        }

        // Check if file should be scanned
        if (!shouldScanFile(fullPath, entry.name, mergedConfig)) {
          continue;
        }

        filesScanned++;
        
        try {
          const content = fs.readFileSync(fullPath, 'utf8');
          const fileReceipts = this.scanContent(fullPath, content, mergedConfig);
          receipts.push(...fileReceipts);
        } catch {
          // Skip files that can't be read
          continue;
        }
      }
    };

    // Handle both file and directory targets
    if (fs.existsSync(targetPath)) {
      const stat = fs.statSync(targetPath);
      if (stat.isDirectory()) {
        walk(targetPath);
      } else {
        filesScanned = 1;
        const content = fs.readFileSync(targetPath, 'utf8');
        const fileReceipts = this.scanContent(targetPath, content, mergedConfig);
        receipts.push(...fileReceipts);
      }
    }

    return {
      screamType: this.screamType,
      receipts,
      filesScanned,
      violationsFound: receipts.length,
      duration: Date.now() - startTime,
    };
  }

  /**
   * Scan file content for theater patterns
   */
  private scanContent(
    filePath: string,
    content: string,
    config: DetectorConfig
  ): ScreamReceipt[] {
    const receipts: ScreamReceipt[] = [];
    const isTestFile = filePath.includes('.test.') || filePath.includes('.spec.');

    // Check placeholder patterns in all files
    for (const [patternName, pattern] of Object.entries(THEATER_PATTERNS)) {
      // Reset regex lastIndex for global patterns
      pattern.lastIndex = 0;
      
      // Some patterns only apply to test files
      if (this.isTestOnlyPattern(patternName as TheaterPatternName) && !isTestFile) {
        continue;
      }
      
      const matches = content.match(pattern);
      if (matches && matches.length > 0) {
        const lineNumbers = this.findLineNumbers(content, pattern);
        
        receipts.push(createScreamReceipt(
          'SCREAM_THEATER',
          2,
          filePath,
          {
            theaterType: patternName,
            pattern: pattern.source,
            matchCount: matches.length,
            lineNumbers,
            severity: this.getSeverityForPattern(patternName as TheaterPatternName),
          },
          config.severity
        ));
      }
    }

    // Test-specific checks
    if (isTestFile) {
      // Check for assertionless tests
      if (this.hasAssertionlessTests(content)) {
        receipts.push(createScreamReceipt(
          'SCREAM_THEATER',
          2,
          filePath,
          {
            theaterType: 'ASSERTIONLESS_TEST',
            message: 'Test file contains tests without assertions',
            severity: 'critical',
          },
          'critical'
        ));
      }

      // Check for mock overuse
      const mockCount = this.countMocks(content);
      if (mockCount > MAX_MOCKS_PER_FILE) {
        receipts.push(createScreamReceipt(
          'SCREAM_THEATER',
          2,
          filePath,
          {
            theaterType: 'MOCK_POISONING',
            mockCount,
            threshold: MAX_MOCKS_PER_FILE,
            message: `File has ${mockCount} mocks, exceeds threshold of ${MAX_MOCKS_PER_FILE}`,
            severity: 'warning',
          },
          'warning'
        ));
      }
    }

    return receipts;
  }

  /**
   * Check if pattern only applies to test files
   */
  private isTestOnlyPattern(patternName: TheaterPatternName): boolean {
    return patternName === 'EMPTY_TEST' || 
           patternName === 'CONSOLE_ONLY_TEST' ||
           patternName === 'SKIP_NO_REASON';
  }

  /**
   * Check if test file has tests without assertions
   */
  private hasAssertionlessTests(content: string): boolean {
    // Find all it() blocks
    const testBlockRegex = /it\s*\([^,]+,\s*(?:async\s*)?\([^)]*\)\s*=>\s*\{([^}]*)\}/g;
    let match;
    
    while ((match = testBlockRegex.exec(content)) !== null) {
      const testBody = match[1];
      // Check if test body has any assertions
      const hasAssertion = /expect\s*\(|assert\.|should\.|toBe|toEqual|toMatch|toThrow|toHaveBeenCalled/i.test(testBody);
      if (!hasAssertion && testBody.trim().length > 0) {
        return true;
      }
    }
    
    return false;
  }

  /**
   * Count mock declarations in file
   */
  private countMocks(content: string): number {
    const mockPatterns = [
      /vi\.mock\s*\(/g,
      /vi\.fn\s*\(/g,
      /vi\.spyOn\s*\(/g,
      /jest\.mock\s*\(/g,
      /jest\.fn\s*\(/g,
      /jest\.spyOn\s*\(/g,
    ];
    
    let count = 0;
    for (const pattern of mockPatterns) {
      const matches = content.match(pattern);
      if (matches) {
        count += matches.length;
      }
    }
    
    return count;
  }

  /**
   * Find line numbers where pattern matches
   */
  private findLineNumbers(content: string, pattern: RegExp): number[] {
    const lines = content.split('\n');
    const lineNumbers: number[] = [];
    
    // Create a new regex without global flag for line-by-line matching
    const flags = pattern.flags.replace('g', '').replace('s', '');
    const linePattern = new RegExp(pattern.source, flags);
    
    for (let i = 0; i < lines.length; i++) {
      if (linePattern.test(lines[i])) {
        lineNumbers.push(i + 1);
      }
    }
    
    return lineNumbers;
  }

  /**
   * Get severity based on pattern type
   */
  private getSeverityForPattern(patternName: TheaterPatternName): 'warning' | 'error' | 'critical' {
    switch (patternName) {
      case 'NOT_IMPLEMENTED':
      case 'STUB_RETURN':
        return 'error';
      case 'EMPTY_TEST':
      case 'CONSOLE_ONLY_TEST':
        return 'critical';
      case 'LOGIC_PLACEHOLDER':
      case 'IMPL_PLACEHOLDER':
      case 'SKIP_NO_REASON':
        return 'warning';
      default:
        return 'error';
    }
  }
}

// --- FACTORY FUNCTION ---

export function createTheaterDetector(): Detector {
  return new TheaterDetector();
}

core/score-classifier.ts.source:
/**
 * P4 RED REGNANT - Score Classifier
 * 
 * @port 4
 * @commander RED_REGNANT
 * @verb SCREAM
 * @provenance: p4-p5-silver-sprint/design.md
 * Validates: Requirements 1.1, 1.2, 1.3, 1.4, 1.5
 * 
 * Classifies mutation scores into FAILURE, GOLDILOCKS, or THEATER
 * and creates cryptographically verifiable SCREAM receipts.
 */

import { z } from 'zod';
import { createHash } from 'crypto';
import { SILVER_STANDARD, ViolationTypeSchema } from '../contracts/index.js';

// --- SCORE CLASSIFICATION ---

export const ScoreClassification = z.enum(['FAILURE', 'GOLDILOCKS', 'THEATER']);
export type ScoreClassification = z.infer<typeof ScoreClassification>;

/**
 * Classify mutation score into FAILURE, GOLDILOCKS, or THEATER
 * 
 * - FAILURE: score < 80% (tests too weak)
 * - GOLDILOCKS: 80% <= score < 99% (just right)
 * - THEATER: score >= 99% (tests are fake/trivial)
 * 
 * @param score - Mutation score (0-100)
 * @returns Classification
 */
export function classifyScore(score: number): ScoreClassification {
  if (typeof score !== 'number' || Number.isNaN(score)) {
    throw new TypeError('Score must be a valid number');
  }
  if (score < 0 || score > 100) {
    throw new RangeError('Score must be between 0 and 100');
  }
  
  if (score < SILVER_STANDARD.MIN_MUTATION_SCORE) {
    return 'FAILURE';
  }
  if (score >= SILVER_STANDARD.THEATER_THRESHOLD) {
    return 'THEATER';
  }
  return 'GOLDILOCKS';
}

// --- SCREAM RECEIPT ---

export const ScreamReceiptSchema = z.object({
  type: z.literal('SCREAM'),
  port: z.literal(4),
  timestamp: z.number(),
  violationType: z.enum(['MUTATION_FAILURE', 'THEATER']),
  score: z.number().min(0).max(100),
  artifact: z.string().min(1),
  receiptHash: z.string().regex(/^sha256:[a-f0-9]{64}$/),
});
export type ScreamReceipt = z.infer<typeof ScreamReceiptSchema>;


/**
 * Create a SCREAM receipt for a violation
 * 
 * Only creates receipts for FAILURE or THEATER scores.
 * GOLDILOCKS scores do not trigger SCREAMs.
 * 
 * @param score - Mutation score that triggered the violation
 * @param artifact - Path to the artifact being evaluated
 * @returns ScreamReceipt with cryptographic hash
 * @throws Error if score is GOLDILOCKS (no violation)
 */
export function createScreamReceipt(
  score: number,
  artifact: string
): ScreamReceipt {
  if (!artifact || artifact.trim().length === 0) {
    throw new TypeError('Artifact path cannot be empty');
  }
  
  const classification = classifyScore(score);
  
  if (classification === 'GOLDILOCKS') {
    throw new Error('Cannot create SCREAM for GOLDILOCKS score - no violation');
  }
  
  const violationType = classification === 'FAILURE' 
    ? 'MUTATION_FAILURE' as const
    : 'THEATER' as const;
  
  const content = {
    type: 'SCREAM' as const,
    port: 4 as const,
    timestamp: Date.now(),
    violationType,
    score,
    artifact: artifact.trim(),
  };
  
  // Compute SHA-256 hash of content
  const hash = createHash('sha256')
    .update(JSON.stringify(content))
    .digest('hex');
  
  return {
    ...content,
    receiptHash: `sha256:${hash}`,
  };
}

/**
 * Verify a SCREAM receipt's integrity
 * 
 * Recomputes the hash from receipt content and compares.
 * Returns false if receipt has been tampered with.
 * 
 * @param receipt - The receipt to verify
 * @returns true if receipt is valid, false if tampered
 */
export function verifyScreamReceipt(receipt: ScreamReceipt): boolean {
  const { receiptHash, ...content } = receipt;
  
  const computed = createHash('sha256')
    .update(JSON.stringify(content))
    .digest('hex');
  
  return `sha256:${computed}` === receiptHash;
}

/**
 * Check if a score requires a SCREAM
 * 
 * @param score - Mutation score to check
 * @returns true if score is FAILURE or THEATER
 */
export function requiresScream(score: number): boolean {
  const classification = classifyScore(score);
  return classification !== 'GOLDILOCKS';
}

