/**
 * P6 KRAKEN KEEPER - Zod Contracts
 * 
 * @port 6
 * @commander KRAKEN_KEEPER
 * @verb ASSIMILATE / STORE
 * @provenance: LEGENDARY_COMMANDERS_V9.md
 * Validates: Requirements 8.1, 8.2, 8.3, 8.4
 */

import { z } from 'zod';

// --- ARCHIVE ENTRY ---

export const ArchiveEntrySchema = z.object({
  id: z.string().uuid(),
  timestamp: z.number(),
  artifact: z.string(),
  fitness: z.number().min(0).max(1),
  cell: z.object({
    x: z.number().int().min(0),
    y: z.number().int().min(0),
  }),
  metadata: z.object({
    generation: z.number().int().min(0),
    sourcePort: z.number().int().min(0).max(7),
    tags: z.array(z.string()).default([]),
  }),
  hash: z.string().regex(/^sha256:[a-f0-9]{64}$/),
});

export type ArchiveEntry = z.infer<typeof ArchiveEntrySchema>;

// --- MAP-ELITE CELL ---

export const MapEliteCellSchema = z.object({
  x: z.number().int().min(0),
  y: z.number().int().min(0),
  elite: ArchiveEntrySchema.optional(),
  history: z.array(z.object({
    id: z.string().uuid(),
    fitness: z.number(),
    timestamp: z.number(),
  })).default([]),
});

export type MapEliteCell = z.infer<typeof MapEliteCellSchema>;

// --- MAP-ELITE ARCHIVE ---

export const MapEliteArchiveSchema = z.object({
  id: z.string().uuid(),
  name: z.string(),
  dimensions: z.object({
    x: z.number().int().min(1),
    y: z.number().int().min(1),
  }),
  cells: z.array(z.array(MapEliteCellSchema)),
  totalEntries: z.number().int().min(0),
  createdAt: z.number(),
  updatedAt: z.number(),
});

export type MapEliteArchive = z.infer<typeof MapEliteArchiveSchema>;

// --- ASSIMILATION RESULT ---

export const AssimilationResultSchema = z.object({
  success: z.boolean(),
  entry: ArchiveEntrySchema.optional(),
  displaced: ArchiveEntrySchema.optional(),
  reason: z.enum(['NEW_ELITE', 'LOWER_FITNESS', 'CELL_OCCUPIED', 'INVALID_ENTRY']),
});

export type AssimilationResult = z.infer<typeof AssimilationResultSchema>;

// --- STORAGE QUERY ---

export const StorageQuerySchema = z.object({
  cellRange: z.object({
    minX: z.number().int().min(0),
    maxX: z.number().int().min(0),
    minY: z.number().int().min(0),
    maxY: z.number().int().min(0),
  }).optional(),
  minFitness: z.number().min(0).max(1).optional(),
  tags: z.array(z.string()).optional(),
  limit: z.number().int().min(1).max(1000).default(100),
});

export type StorageQuery = z.infer<typeof StorageQuerySchema>;

// --- MAP-ELITE FUNCTIONS ---

/**
 * Creates an empty MAP-ELITE archive
 */
export function createArchive(name: string, dimX: number, dimY: number): MapEliteArchive {
  const cells: MapEliteCell[][] = [];
  for (let y = 0; y < dimY; y++) {
    const row: MapEliteCell[] = [];
    for (let x = 0; x < dimX; x++) {
      row.push({ x, y, elite: undefined, history: [] });
    }
    cells.push(row);
  }
  
  const now = Date.now();
  return {
    id: crypto.randomUUID(),
    name,
    dimensions: { x: dimX, y: dimY },
    cells,
    totalEntries: 0,
    createdAt: now,
    updatedAt: now,
  };
}

/**
 * Assimilates an entry into the archive (MAP-ELITE algorithm)
 * Only stores if fitness is higher than current elite in cell
 */
export function assimilate(archive: MapEliteArchive, entry: ArchiveEntry): AssimilationResult {
  const { x, y } = entry.cell;
  
  // Bounds check
  if (x >= archive.dimensions.x || y >= archive.dimensions.y) {
    return { success: false, reason: 'INVALID_ENTRY' };
  }
  
  const cell = archive.cells[y][x];
  const currentElite = cell.elite;
  
  // If no current elite, this becomes the elite
  if (!currentElite) {
    cell.elite = entry;
    cell.history.push({ id: entry.id, fitness: entry.fitness, timestamp: entry.timestamp });
    archive.totalEntries++;
    archive.updatedAt = Date.now();
    return { success: true, entry, reason: 'NEW_ELITE' };
  }
  
  // If new entry has higher fitness, replace
  if (entry.fitness > currentElite.fitness) {
    cell.elite = entry;
    cell.history.push({ id: entry.id, fitness: entry.fitness, timestamp: entry.timestamp });
    archive.updatedAt = Date.now();
    return { success: true, entry, displaced: currentElite, reason: 'NEW_ELITE' };
  }
  
  // Lower fitness, don't store
  return { success: false, reason: 'LOWER_FITNESS' };
}

/**
 * Retrieves the elite from a specific cell
 */
export function getElite(archive: MapEliteArchive, x: number, y: number): ArchiveEntry | undefined {
  if (x >= archive.dimensions.x || y >= archive.dimensions.y) {
    return undefined;
  }
  return archive.cells[y][x].elite;
}

/**
 * Queries the archive for entries matching criteria
 */
export function queryArchive(archive: MapEliteArchive, query: StorageQuery): ArchiveEntry[] {
  const results: ArchiveEntry[] = [];
  
  const minX = query.cellRange?.minX ?? 0;
  const maxX = query.cellRange?.maxX ?? archive.dimensions.x - 1;
  const minY = query.cellRange?.minY ?? 0;
  const maxY = query.cellRange?.maxY ?? archive.dimensions.y - 1;
  
  for (let y = minY; y <= maxY && y < archive.dimensions.y; y++) {
    for (let x = minX; x <= maxX && x < archive.dimensions.x; x++) {
      const elite = archive.cells[y][x].elite;
      if (!elite) continue;
      
      // Filter by fitness
      if (query.minFitness !== undefined && elite.fitness < query.minFitness) continue;
      
      // Filter by tags
      if (query.tags && query.tags.length > 0) {
        const hasAllTags = query.tags.every(tag => elite.metadata.tags.includes(tag));
        if (!hasAllTags) continue;
      }
      
      results.push(elite);
      if (results.length >= query.limit) return results;
    }
  }
  
  return results;
}

hot_obsidian_sandbox/bronze/contracts/ve.source:
/**
 * Cross-Commander Verb Enforcement
 * 
 * @provenance: LEGENDARY_COMMANDERS_V9.md
 * Validates: Property 14 (Verb Enforcement)
 * 
 * Each commander has assigned verbs. Cross-verb calls trigger P4 screams.
 */

import { z } from 'zod';

// --- COMMANDER VERB MAPPING ---

export const COMMANDER_VERBS = {
  0: ['OBSERVE', 'SENSE'],           // P0: Lidless Legion
  1: ['BRIDGE', 'FUSE'],             // P1: Web Weaver
  2: ['SHAPE', 'TRANSFORM'],         // P2: Mirror Magus
  3: ['INJECT', 'DELIVER'],          // P3: Spore Storm
  4: ['DISRUPT', 'SING', 'SCREAM'],  // P4: Red Regnant
  5: ['DANCE', 'DIE', 'IMMUNIZE'],   // P5: Pyre Praetorian
  6: ['ASSIMILATE', 'STORE'],        // P6: Kraken Keeper
  7: ['DECIDE', 'NAVIGATE'],         // P7: Spider Sovereign
} as const;

export type Port = 0 | 1 | 2 | 3 | 4 | 5 | 6 | 7;
export type Verb = typeof COMMANDER_VERBS[Port][number];

// --- HIVE/8 PAIRINGS (Anti-diagonal: sum = 7) ---

export const HIVE_PAIRINGS = {
  H: [0, 7] as const, // Hunt: Lidless Legion + Spider Sovereign
  I: [1, 6] as const, // Interlock: Web Weaver + Kraken Keeper
  V: [2, 5] as const, // Validate: Mirror Magus + Pyre Praetorian
  E: [3, 4] as const, // Evolve: Spore Storm + Red Regnant
} as const;

// --- PREY/8 PAIRINGS (Serpentine) ---

export const PREY_PAIRINGS = {
  P: [0, 6] as const, // Perceive: Lidless Legion + Kraken Keeper
  R: [1, 7] as const, // React: Web Weaver + Spider Sovereign
  E: [2, 4] as const, // Execute: Mirror Magus + Red Regnant
  Y: [3, 5] as const, // Yield: Spore Storm + Pyre Praetorian
} as const;

// --- VERB VIOLATION SCHEMA ---

export const VerbViolationSchema = z.object({
  timestamp: z.number(),
  sourcePort: z.number().min(0).max(7),
  attemptedVerb: z.string(),
  allowedVerbs: z.array(z.string()),
  message: z.string(),
});

export type VerbViolation = z.infer<typeof VerbViolationSchema>;

// --- ENFORCEMENT FUNCTIONS ---

/**
 * Checks if a verb is allowed for a given port
 */
export function isVerbAllowed(port: Port, verb: string): boolean {
  const allowed = COMMANDER_VERBS[port] as readonly string[];
  return allowed.includes(verb);
}

/**
 * Gets allowed verbs for a port
 */
export function getAllowedVerbs(port: Port): readonly string[] {
  return COMMANDER_VERBS[port];
}

/**
 * Creates a verb violation record
 */
export function createVerbViolation(port: Port, attemptedVerb: string): VerbViolation {
  const allowed = getAllowedVerbs(port);
  return {
    timestamp: Date.now(),
    sourcePort: port,
    attemptedVerb,
    allowedVerbs: [...allowed],
    message: `Port ${port} attempted verb "${attemptedVerb}" but only allows: ${allowed.join(', ')}`,
  };
}

/**
 * Validates HIVE/8 anti-diagonal property (sum = 7)
 */
export function isValidHivePair(portA: Port, portB: Port): boolean {
  return portA + portB === 7;
}

/**
 * Gets the HIVE phase for a port pair
 */
export function getHivePhase(portA: Port, portB: Port): 'H' | 'I' | 'V' | 'E' | null {
  if (!isValidHivePair(portA, portB)) return null;
  
  const sorted = [portA, portB].sort((a, b) => a - b) as [Port, Port];
  
  for (const [phase, pair] of Object.entries(HIVE_PAIRINGS)) {
    const pairSorted = [...pair].sort((a, b) => a - b);
    if (sorted[0] === pairSorted[0] && sorted[1] === pairSorted[1]) {
      return phase as 'H' | 'I' | 'V' | 'E';
    }
  }
  return null;
}

/**
 * Gets the PREY phase for a port pair
 */
export function getPreyPhase(portA: Port, portB: Port): 'P' | 'R' | 'E' | 'Y' | null {
  const sorted = [portA, portB].sort((a, b) => a - b) as [Port, Port];
  
  for (const [phase, pair] of Object.entries(PREY_PAIRINGS)) {
    const pairSorted = [...pair].sort((a, b) => a - b);
    if (sorted[0] === pairSorted[0] && sorted[1] === pairSorted[1]) {
      return phase as 'P' | 'R' | 'E' | 'Y';
    }
  }
  return null;
}

hot_obsidian_sandbox/bronze/P2_MIRROR_MA.source:
/**
 * P2 MIRROR MAGUS - Zod Contracts
 * 
 * @port 2
 * @commander MIRROR_MAGUS
 * @verb SHAPE / TRANSFORM
 * @provenance: LEGENDARY_COMMANDERS_V9.md
 * Validates: Requirements 4.1, 4.2, 4.3, 4.4, 4.5
 */

import { z } from 'zod';

// --- TRANSFORMATION TYPE ---

export const TransformationTypeSchema = z.enum([
  'ONE_EURO_FILTER',  // Noise reduction
  'SCHEMA_MORPH',     // Schema transformation
  'NORMALIZE',        // Data normalization
  'DENORMALIZE',      // Data denormalization
  'AGGREGATE',        // Data aggregation
  'SPLIT',            // Data splitting
]);

export type TransformationType = z.infer<typeof TransformationTypeSchema>;

// --- TRANSFORMATION SCHEMA ---

export const TransformationSchema = z.object({
  id: z.string().uuid(),
  timestamp: z.number(),
  type: TransformationTypeSchema,
  input: z.unknown(),
  output: z.unknown(),
  invertible: z.boolean(),
  metadata: z.object({
    latencyMs: z.number().min(0).optional(),
    lossless: z.boolean().default(true),
    parameters: z.record(z.unknown()).default({}),
  }),
});

export type Transformation = z.infer<typeof TransformationSchema>;

// --- ONE EURO FILTER CONFIG ---

export const OneEuroFilterConfigSchema = z.object({
  minCutoff: z.number().min(0).default(1.0),
  beta: z.number().min(0).default(0.007),
  dCutoff: z.number().min(0).default(1.0),
  frequency: z.number().min(0).default(120),
});

export type OneEuroFilterConfig = z.infer<typeof OneEuroFilterConfigSchema>;

// --- ONE EURO FILTER STATE ---

export const OneEuroFilterStateSchema = z.object({
  x: z.number(),
  dx: z.number(),
  lastTime: z.number(),
  initialized: z.boolean(),
});

export type OneEuroFilterState = z.infer<typeof OneEuroFilterStateSchema>;

// --- SHAPE RESULT ---

export const ShapeResultSchema = z.object({
  success: z.boolean(),
  transformation: TransformationSchema.optional(),
  error: z.string().optional(),
  canInvert: z.boolean().default(false),
});

export type ShapeResult = z.infer<typeof ShapeResultSchema>;

// --- SCHEMA MORPH ---

export const SchemaMorphSchema = z.object({
  sourceSchema: z.string(),
  targetSchema: z.string(),
  mappings: z.array(z.object({
    sourcePath: z.string(),
    targetPath: z.string(),
    transform: z.string().optional(),
  })),
  bidirectional: z.boolean().default(false),
});

export type SchemaMorph = z.infer<typeof SchemaMorphSchema>;

// --- ONE EURO FILTER IMPLEMENTATION ---

/**
 * Smoothing factor calculation for One Euro Filter
 */
export function smoothingFactor(te: number, cutoff: number): number {
  const r = 2 * Math.PI * cutoff * te;
  return r / (r + 1);
}

/**
 * Exponential smoothing
 */
export function exponentialSmoothing(a: number, x: number, xPrev: number): number {
  return a * x + (1 - a) * xPrev;
}

/**
 * Creates initial One Euro Filter state
 */
export function createFilterState(): OneEuroFilterState {
  return {
    x: 0,
    dx: 0,
    lastTime: 0,
    initialized: false,
  };
}

/**
 * Applies One Euro Filter to a value
 * Returns [filteredValue, newState]
 */
export function applyOneEuroFilter(
  value: number,
  timestamp: number,
  state: OneEuroFilterState,
  config: OneEuroFilterConfig
): [number, OneEuroFilterState] {
  if (!state.initialized) {
    return [value, {
      x: value,
      dx: 0,
      lastTime: timestamp,
      initialized: true,
    }];
  }
  
  const te = timestamp - state.lastTime;
  if (te <= 0) {
    return [state.x, state];
  }
  
  // Estimate derivative
  const edx = (value - state.x) / te;
  const adx = smoothingFactor(te, config.dCutoff);
  const dx = exponentialSmoothing(adx, edx, state.dx);
  
  // Adaptive cutoff
  const cutoff = config.minCutoff + config.beta * Math.abs(dx);
  const ax = smoothingFactor(te, cutoff);
  const x = exponentialSmoothing(ax, value, state.x);
  
  return [x, {
    x,
    dx,
    lastTime: timestamp,
    initialized: true,
  }];
}

// --- TRANSFORMATION FUNCTIONS ---

/**
 * Creates a transformation record
 */
export function createTransformation(
  type: TransformationType,
  input: unknown,
  output: unknown,
  invertible: boolean,
  parameters?: Record<string, unknown>
): Transformation {
  return {
    id: crypto.randomUUID(),
    timestamp: Date.now(),
    type,
    input,
    output,
    invertible,
    metadata: {
      lossless: invertible,
      parameters: parameters ?? {},
    },
  };
}

/**
 * Validates that a transformation is invertible by checking round-trip
 */
export function isRoundTripValid<T>(
  value: T,
  transform: (v: T) => T,
  inverse: (v: T) => T,
  equals: (a: T, b: T) => boolean
): boolean {
  const transformed = transform(value);
  const restored = inverse(transformed);
  return equals(value, restored);
}

