import { describe, it, expect, beforeEach, vi } from "vitest";
import * as fs from "node:fs";
import * as path from "node:path";
import * as crypto from "node:crypto";
import { fileURLToPath } from "node:url";
import { 
    scream, 
    checkRootPollution, 
    checkMutationProof, 
    auditContent, 
    scanMedallions, 
    checkLedgerIntegrity,
    checkManifestIntegrity,
    violations, 
    clearViolations,
    main,
    CacaoPlaybookSchema,
    ManifestSchema
} from "./psychic_scream";

const __dirname = path.dirname(fileURLToPath(import.meta.url));

describe("Psychic Scream (Negative Trust Protocol)", () => {
    beforeEach(() => {
        clearViolations();
    });

    describe("scream", () => {
        it("should log exact messages and verify blackboard content", () => {
            const tempBlackboard = path.join(__dirname, "temp_blackboard.jsonl");
            if (fs.existsSync(tempBlackboard)) fs.unlinkSync(tempBlackboard);

            const consoleSpy = vi.spyOn(console, "error").mockImplementation(() => {});
            const violation = { type: "TEST_TYPE", file: "test.ts", message: "Test message" };
            scream(violation, tempBlackboard);

            expect(consoleSpy).toHaveBeenCalledWith(expect.stringContaining("[TEST_TYPE] in test.ts"));
            expect(consoleSpy).toHaveBeenCalledWith(expect.stringContaining("Test message"));

            expect(violations).toHaveLength(1);
            expect(violations[0]).toEqual(violation);

            const content = fs.readFileSync(tempBlackboard, "utf8");
            expect(content.endsWith('\n')).toBe(true); // Kill newline mutant

            const entry = JSON.parse(content.trim());
            expect(entry.violationType).toBe("TEST_TYPE");
            expect(entry.file).toBe("test.ts");
            expect(entry.msg).toBe("Test message");
            expect(entry.mark).toBe("PSYCHIC_SCREAM_VIOLATION");
            expect(entry.type).toBe("SCREAM");
            expect(entry.hive).toBe("HFO_GEN88");
            expect(entry.port).toBe(4);
            expect(entry.gen).toBe(88);
            expect(entry.ts).toBeDefined();

            fs.unlinkSync(tempBlackboard);
            consoleSpy.mockRestore();
        });

        it("should handle blackboard write errors gracefully", () => {
            const consoleSpy = vi.spyOn(console, "error").mockImplementation(() => {});
            // Passing a directory as the blackboard path should cause an error
            scream({ type: "ERR", file: "f", message: "m" }, __dirname);
            expect(consoleSpy).toHaveBeenCalled();
            consoleSpy.mockRestore();
        });
    });

    describe("checkRootPollution", () => {
        it("should detect root pollution with exact message", () => {
            checkRootPollution(["bad.exe", "good.md"]);
            expect(violations.some(v => v.type === "POLLUTION" && v.file === "bad.exe")).toBe(true);
            expect(violations.find(v => v.file === "bad.exe")?.message).toBe("Root pollution detected: bad.exe is not allowed in the cleanroom root.");
        });

        it("should allow all files in the allowed list", () => {
            const allowed = [
                'hot_obsidian_sandbox', 'cold_obsidian_sandbox', 'AGENTS.md', 'llms.txt',
                'obsidianblackboard.jsonl', 'package.json', 'package-lock.json',
                'stryker.root.config.mjs', 'vitest.root.config.ts', '.git', '.gitignore',
                '.vscode', '.env', '.stryker-tmp', 'node_modules'
            ];
            checkRootPollution(allowed);
            expect(violations.filter(v => v.type === "POLLUTION")).toHaveLength(0);
        });

        it("should allow ttao-notes with regex", () => {
            checkRootPollution(["ttao-notes-2025.md"]);
            expect(violations.filter(v => v.type === "POLLUTION")).toHaveLength(0);
        });

        it("should fail if any pattern fails", () => {
            checkRootPollution(["bad.exe", "AGENTS.md"]);
            expect(violations.some(v => v.type === "POLLUTION" && v.file === "bad.exe")).toBe(true);

            violations.length = 0;
            checkRootPollution(["AGENTS.md", "bad.exe"]);
            expect(violations.some(v => v.type === "POLLUTION" && v.file === "bad.exe")).toBe(true);
        });
    });

    describe("checkMutationProof", () => {
        it("should handle missing report with warning", () => {
            const consoleSpy = vi.spyOn(console, "warn").mockImplementation(() => {});
            checkMutationProof("nonexistent.json");
            expect(consoleSpy).toHaveBeenCalledWith(expect.stringContaining("Skipping mutation check"));
            consoleSpy.mockRestore();
        });

        it("should handle missing mutants array in report", () => {
            const tempReport = path.join(__dirname, "no_mutants.json");
            fs.writeFileSync(tempReport, JSON.stringify({ files: { "file.ts": { mutants: null } } }));
            checkMutationProof(tempReport);
            expect(violations.filter(v => v.type === "MUTATION_FAILURE")).toHaveLength(0);
            fs.unlinkSync(tempReport);
        });

        it("should skip files with 0 mutants", () => {
            const tempReport = path.join(__dirname, "zero_mutants.json");
            fs.writeFileSync(tempReport, JSON.stringify({ files: { "file.ts": { mutants: [] } } }));
            checkMutationProof(tempReport);
            expect(violations.filter(v => v.type === "MUTATION_FAILURE")).toHaveLength(0);
            fs.unlinkSync(tempReport);
        });

        it("should handle malformed JSON", () => {
            const tempFile = path.join(__dirname, "malformed.json");
            fs.writeFileSync(tempFile, "{ invalid }");
            checkMutationProof(tempFile);
            expect(violations.some(v => v.type === "MUTATION_FAILURE")).toBe(true);
            expect(violations.find(v => v.file === tempFile)?.message).toContain("Failed to parse mutation report");
            fs.unlinkSync(tempFile);
        });

        it("should detect mutation failure with exact message", () => {
            const tempFile = path.join(__dirname, "fail.json");
            const report = {
                files: {
                    "src/fail.ts": {
                        mutants: [
                            { status: "Killed" },
                            { status: "Survived" }
                        ]
                    }
                }
            };
            fs.writeFileSync(tempFile, JSON.stringify(report));
            checkMutationProof(tempFile);
            const v = violations.find(v => v.file === "src/fail.ts");
            expect(v?.message).toBe("Mutation score 50.00% is below the 80% threshold.");
            expect(v?.type).toBe("MUTATION_FAILURE"); // Kill type mutant
            fs.unlinkSync(tempFile);
        });

        it("should count Timeout as Killed", () => {
            const tempFile = path.join(__dirname, "timeout.json");
            const report = {
                files: {
                    "src/timeout.ts": {
                        mutants: [
                            { status: "Killed" },
                            { status: "Timeout" },
                            { status: "Survived" }
                        ]
                    }
                }
            };
            fs.writeFileSync(tempFile, JSON.stringify(report));
            checkMutationProof(tempFile);
            expect(violations.find(v => v.file === "src/timeout.ts")?.message).toBe("Mutation score 66.67% is below the 80% threshold.");
            fs.unlinkSync(tempFile);
        });

        it("should detect theater with exact message", () => {
            const tempFile = path.join(__dirname, "theater.json");
            const report = {
                files: {
                    "src/win.ts": {
                        mutants: [
                            { status: "Killed" }
                        ]
                    }
                }
            };
            fs.writeFileSync(tempFile, JSON.stringify(report));
            checkMutationProof(tempFile);
            expect(violations.find(v => v.file === "src/win.ts")?.type).toBe("THEATER");
            expect(violations.find(v => v.file === "src/win.ts")?.message).toBe("Mutation score 100% detected. This is \"Theater\" and is forbidden.");
            fs.unlinkSync(tempFile);
        });

        it("should skip files with 0 mutants", () => {
            const tempFile = path.join(__dirname, "zero.json");
            const report = {
                files: {
                    "src/zero.ts": {
                        mutants: []
                    }
                }
            };
            fs.writeFileSync(tempFile, JSON.stringify(report));
            checkMutationProof(tempFile);
            expect(violations.filter(v => v.file === "src/zero.ts")).toHaveLength(0);
            fs.unlinkSync(tempFile);
        });

        it("should allow exactly 80% score", () => {
            const tempFile = path.join(__dirname, "eighty.json");
            const report = {
                files: {
                    "src/eighty.ts": {
                        mutants: [
                            { status: "Killed" }, { status: "Killed" }, { status: "Killed" }, { status: "Killed" },
                            { status: "Survived" }
                        ]
                    }
                }
            };
            fs.writeFileSync(tempFile, JSON.stringify(report));
            checkMutationProof(tempFile);
            expect(violations.filter(v => v.file === "src/eighty.ts")).toHaveLength(0);
            fs.unlinkSync(tempFile);
        });

        it("should handle report with total 0 mutants explicitly to kill mutant", () => {
            const tempFile = path.join(__dirname, "zero_total.json");
            const report = {
                files: {
                    "src/zero.ts": {
                        mutants: []
                    }
                }
            };
            fs.writeFileSync(tempFile, JSON.stringify(report));
            checkMutationProof(tempFile, ["src/zero.ts"]);
            expect(violations.length).toBe(0);
            fs.unlinkSync(tempFile);
        });
    });

    describe("auditContent", () => {
        it("should detect amnesia and bespoke types", () => {
            const silverFile = path.join(__dirname, "silver", "test.ts");
            const bronzeFile = path.join(__dirname, "bronze", "test.ts");
            fs.mkdirSync(path.dirname(silverFile), { recursive: true });
            fs.mkdirSync(path.dirname(bronzeFile), { recursive: true });

            const silverContent = "console.log(\"debug\");\nconst x: any = 1;";
            const bronzeContent = "const x: any = 1;";

            fs.writeFileSync(silverFile, silverContent);
            fs.writeFileSync(bronzeFile, bronzeContent);

            auditContent(silverFile, silverContent);
            expect(violations.some(v => v.type === "AMNESIA" && v.message.includes("Debug logs"))).toBe(true);
            expect(violations.some(v => v.type === "BESPOKE" && v.message.includes("Bespoke 'any' type"))).toBe(true);

            violations.length = 0;
            auditContent(bronzeFile, bronzeContent);
            expect(violations.some(v => v.type === "BESPOKE")).toBe(true);
            expect(violations.some(v => v.type === "AMNESIA")).toBe(false);

            fs.unlinkSync(silverFile);
            fs.unlinkSync(bronzeFile);
        });

        it("should detect missing provenance in silver", () => {
            const silverFile = path.join(__dirname, "silver", "test.ts");
            fs.mkdirSync(path.dirname(silverFile), { recursive: true });
            const content = "const x = 1;";
            fs.writeFileSync(silverFile, content);
            auditContent(silverFile, content);
            expect(violations.some(v => v.type === "VIOLATION" && v.message.includes("provenance"))).toBe(true);
            fs.unlinkSync(silverFile);
        });
    });

    describe("scanMedallions", () => {
        it("should detect missing tests and handle existing ones", () => {
            const tempMed = path.join(__dirname, "temp_med_nested");
            const subDir = path.join(tempMed, "sub");
            fs.mkdirSync(subDir, { recursive: true });

            const logicFile = path.join(subDir, "logic.ts");
            const testFile = path.join(subDir, "logic.test.ts");
            const orphanFile = path.join(subDir, "orphan.ts");

            fs.writeFileSync(logicFile, "export const x = 1;");
            fs.writeFileSync(testFile, "import { x } from './logic';");
            fs.writeFileSync(orphanFile, "export const y = 2;");

            scanMedallions([tempMed]);

            const v = violations.find(v => v.file.includes("orphan.ts") && v.type === "THEATER");
            expect(v).toBeDefined();
            expect(v?.message).toBe("Silver/Gold artifact missing corresponding test file.");

            fs.rmSync(tempMed, { recursive: true, force: true });
        });

        it("should handle complex filenames", () => {
            const tempMed = path.join(__dirname, "temp_med_complex");
            fs.mkdirSync(tempMed, { recursive: true });
            const complexFile = path.join(tempMed, "logic.ts.ts");
            fs.writeFileSync(complexFile, "export const x = 1;");

            scanMedallions([tempMed]);
            expect(violations.some(v => v.file.includes("logic.ts.ts") && v.type === "THEATER")).toBe(true);

            fs.rmSync(tempMed, { recursive: true, force: true });
        });

        it("should ignore .d.ts files", () => {
            const tempMed = path.join(__dirname, "temp_med_dts");
            fs.mkdirSync(tempMed, { recursive: true });
            const dtsFile = path.join(tempMed, "types.d.ts");
            fs.writeFileSync(dtsFile, "export type X = number;");

            scanMedallions([tempMed]);
            expect(violations.filter(v => v.file.includes("types.d.ts"))).toHaveLength(0);

            fs.rmSync(tempMed, { recursive: true, force: true });
        });
    });

    describe("checkLedgerIntegrity", () => {
        it("should detect missing ledger", () => {
            checkLedgerIntegrity("nonexistent.jsonl");
            expect(violations.some(v => v.message.includes("missing"))).toBe(true);
        });

        it("should detect hash mismatch and chain breaks", () => {
